   public miUsuario: Usuario;

  constructor(){

    this.miUsuario = new Usuario( 1, "Irene", "Herrero Becker", "irenisima82@hotmail.com","https://i.pinimg.com/280x280_RS/71/7a/08/717a08bf7a48049f3e721cfdbf5daabb.jpg","Serpiente");
