import { Component } from '@angular/core';
import { Usuario } from 'src/app/models/usuario';
import { UsuariosService } from 'src/app/shared/usuarios.service';

@Component({
  selector: 'app-formulario-registro',
  templateUrl: './formulario-registro.component.html',
  styleUrls: ['./formulario-registro.component.css']
})
export class FormularioRegistroComponent {

  public usuarios: Usuario[];

  constructor(private usuariosService: UsuariosService) {}

  public postUser(nuevoNombre: string, nuevoApellidos: string, nuevoCorreo: string, nuevoFoto: string, nuevoPassword: string, nuevoPasswordmatch:string){  /// registrar usuario

       if(nuevoPassword == nuevoPasswordmatch){ //tiene que haber un number, lo pongo por el id_usuario de la bbdd, porque si no da error)

        this.usuariosService.register(new Usuario(0,nuevoNombre, nuevoApellidos, nuevoCorreo, nuevoFoto, nuevoPassword)).subscribe((data)=>{

          console.log(data);
        })
       }else{

        console.log("Las contraseñas NO coinciden.")
       }

  }

}
<div class="formulario">
<!-- hacer el *ngfor / usuario de usuarios-->
<div class="cajita">


    <p class="titulo">Registraté:</p>

    <p class="boton3">Nombre:</p>
    <input  class="input" #nombre>

    <p class="boton3">Apellidos:</p>
    <input  class="input" #apellidos>

    <p class="boton3">Correo:</p>
    <input  class="input" #correo>

    <p class="boton3">foto:</p>
    <input  class="input" #foto>

    <p class="boton3">password:</p>
    <input  class="input" #password>

    <button class="boton2" (click)=" postUser(nombre.value, apellidos.value, correo.value, foto.value, password.value, password.value)">Registrarse</button>

    </div>

 </div>
